javascript:
let apps=[];
getAppName();
frappe.query_reports["Test Execution Statistics"] = {
    "filters": [{
        "fieldname": "app_name",
        "label": __("App"),
        "fieldtype": "Select",
        "default": '',
        "reqd": 1,
        "options":apps
    }]
}

function getAppName() {
apps=[];
    frappe.call({
        method: 'frappe.desk.reportview.get',
        args: {
            doctype: 'Module Def',
            fields: ["`tabModule Def`.`app_name`"],
            order_by: "`tabModule Def`.`modified` desc",
            start: 0,
            page_length: 2000,
            with_comment_count: true
        },
        freeze: true,
 freeze_message: 'Loading report please wait',
        async: false,
        callback: function(r) {
            if (!r.exc) {
                let all_apps = r.message.values;
                all_apps.forEach((a) => {
                    let app_name = a[0];
                    if (!apps.includes(app_name)) {
                        apps.push(app_name);
                    }
                });

                return apps;
            }
        }
    });
}
